import Map from "../Map/Map";
import HeroText from "./HeroText";
import classes from "./Home.module.css";
import Signup from "../EntryForm/Signup";
import Login from "../EntryForm/Login";
import Card from "../UI/Card/Card";
import vision from "../../photos/7.jpg";
import Button from "../UI/Button/Button";
import faqImage from "../../photos/faq.png";
import addAlert from "../../photos/waste.jpg";
import Faq from "../Pages/FAQ";
import ContactForm from "../Pages/ContactForm";
const Home = (props) => {
  const submitUserHandler = async (payload) => {
    console.log(JSON.stringify(payload));
    const response = await fetch(
      "https://waste-eliminator-api.us-south.cf.appdomain.cloud/users",
      {
        method: "POST",
        body: JSON.stringify(payload),
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    try {
      const data = await response.json();
      props.setToken(data.token);
    } catch (err) {
      console.log(err);
    }
  };

  const loginUserHandler = async (payload) => {
    const response = await fetch(
      "https://waste-eliminator-api.us-south.cf.appdomain.cloud/users/login",
      {
        method: "POST",
        body: JSON.stringify(payload),
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    try {
      const data = await response.json();
      props.setToken(data.token);
    } catch (err) {
      console.log(err);
    }
  };

  const signupScreen = props.signupIsShown && (
    <Signup
      onConfirm={submitUserHandler}
      onShowLogin={props.showLoginHandler}
      onClose={props.hideEntryFormHandler}
    />
  );

  const loginScreen = props.loginIsShown && (
    <Login
      onConfirm={loginUserHandler}
      onShowSignup={props.showSignupHandler}
      onClose={props.hideEntryFormHandler}
    />
  );



  const Vision = (
    <Card className={classes.vision}>
      <div>
        <h1>VISION</h1>
        <p>
          We all want a waste free planet but very few are taking actions to
          keep it clean. With your help we plan to achieve this goal. Be a part
          of the solution, not part of the pollution.
        </p>
        <Button>Learn More</Button>
      </div>
      <img src={vision} height="300px" width="300px" alt="4" />
    </Card>
  );

  return (
    <div className={classes.home}>
      <Map height="100%" width="100%" />
      <HeroText onShowSignup={props.showSignupHandler} />
      {signupScreen}
      {loginScreen}
      {Vision}
      <Card className={classes.addAlert}>
        <img
          src={addAlert}
          height="300px"
          width="300px"
          alt="add an waste alert"
        />
        <div>
          <h1>Add an alert</h1>
          <p>Add photo and location of waste around you.</p>
          <Button className={classes.button}><span className={classes.add}>+  Post Now</span></Button>
        </div>
      </Card>
      <Card className={classes.faq}>
        <Faq />
        <img src={faqImage} height="250px" width="450px" alt="faq" />
      </Card>
      <Card className={classes.contact}>
        <ContactForm />
      </Card>
    </div>
  );
};

export default Home;
