import { useState } from "react";

import Header from "./components/Header/Header";
import Home from "./components/Home/Home";
import { Fragment } from "react";
import useToken from "./components/Hooks/useToken";
import Signup from "./components/EntryForm/Signup";
import Login from "./components/EntryForm/Login";


import { BrowserRouter, Route, Switch } from "react-router-dom";
import AboutUs from "./components/Pages/AboutUs";
import Alert from "./components/Alert/Alert";

function App() {
  const [loginIsShown, setLoginIsShown] = useState(false);
  const [signupIsShown, setSignupIsShown] = useState(false);

  const { token, setToken } = useToken();

  const showLoginHandler = () => {
    setLoginIsShown(true);
  };

  const hideEntryFormHandler = () => {
    setLoginIsShown(false);
    setSignupIsShown(false);
  };
  const showSignupHandler = () => {
    setSignupIsShown(true);
  };

  const submitUserHandler = async (payload) => {
    console.log(JSON.stringify(payload));
    const response = await fetch(
      "https://waste-eliminator-api.us-south.cf.appdomain.cloud/users",
      {
        method: "POST",
        body: JSON.stringify(payload),
        headers: {
          "Content-Type": "application/json",
        },
      }
    );

    try {
      const data = await response.json();
      setToken(data.token);
    } catch (err) {
      console.log(err);
    }
  };

  const loginUserHandler = async (payload) => {
    const response = await fetch(
      "https://waste-eliminator-api.us-south.cf.appdomain.cloud/users/login",
      {
        method: "POST",
        body: JSON.stringify(payload),
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
    try {
      const data = await response.json();
      setToken(data.token);
    } catch (err) {
      console.log(err);
    }
  };

  const signupScreen = signupIsShown && (
    <Signup
      onConfirm={submitUserHandler}
      onShowLogin={showLoginHandler}
      onClose={hideEntryFormHandler}
    />
  );

  const loginScreen = loginIsShown && (
    <Login
      onConfirm={loginUserHandler}
      onShowSignup={showSignupHandler}
      onClose={hideEntryFormHandler}
    />
  );


  return (
    <Fragment>
      <Header
        token={token}
        setToken={setToken}
        onShowLogin={showLoginHandler}
        onShowSignup={showSignupHandler}
      />

      {signupScreen}
      {loginScreen}

      <BrowserRouter>
        <Switch>
          {
            <Route path="/about">
              <AboutUs />
            </Route>
          }

          {
            <Route path="/alert">
              <Alert token={token} />
            </Route>
          }

          <Route path="/">
            {/* <Home
              setToken={setToken}
              showLoginHandler={showLoginHandler}
              showSignupHandler={showSignupHandler}
              hideEntryFormHandler={hideEntryFormHandler}
              loginIsShown={loginIsShown}
              signupIsShown={signupIsShown}
            /> */}
          </Route>
        </Switch>
      </BrowserRouter>

      {false && <Fragment></Fragment>}
    </Fragment>
  );
}

export default App;
