import Button from "../UI/Button/Button";
import classes from "./AddPost.module.css";

import { useRef, useState } from "react";


const latReducer = (state, action) => {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: !isNaN(action.val) };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: !isNaN(action.val) };
  }
  return { value: "", isValid: false };
};
const longReducer = (state, action) => {
  if (action.type === "USER_INPUT") {
    return { value: action.val, isValid: !isNaN(action.val) };
  }
  if (action.type === "INPUT_BLUR") {
    return { value: state.value, isValid: !isNaN(action.val) };
  }
  return { value: "", isValid: false };
};

const AddPost = (props) => {
  const wasteTypeRef = useRef();
  const latRef = useRef();
  const longRef = useRef();

  const [latState, dispatchLat] = useReducer(latReducer, {
    value: "",
    isValid: false,
  });

  const [longState, dispatchLong] = useReducer(latReducer, {
    value: "",
    isValid: null,
  });

  const { isValid: latIsValid } = latState;
  const { isValid: longIsValid } = longState;

  useEffect(() => {
    const identifier = setTimeout(() => {
      console.log("Checking form validity");
      setFormIsValid(latIsValid && longIsValid);
    }, 500);

    return () => {
      console.log("CLEANUP");
      clearTimeout(identifier);
    };
  }, [latIsValid, longIsValid]);

  const [file, setFile] = useState(null);


  const SubmitHandler = (event) => {
    event.preventDefault();

    const enteredWasteType = wasteTypeRef.current.value;
    const enteredLat = parseFloat(latRef.current.value);
    const enteredLong = parseFloat(longRef.current.value);

    const metaData = {
      "wasteType": enteredWasteType,
      "location": {
        "lat": enteredLat,
        "long": enteredLong,
      }
    };
    const data = new FormData();
    data.append("image", file);
    data.append("data", JSON.stringify(metaData));



    props.onConfirm(data);
  };

  const fileHandler = (event) => {
    setFile(event.target.files[0]);
  };

  return (
    <form onSubmit={SubmitHandler} className={classes.form}>
      <div>
        <label htmlFor="wastes">Choose Waste Type</label>
        <select type="text" id="wastes" name="wastes" ref={wasteTypeRef}>
          <option value="Organic">Organic</option>
          <option value="Hazardous">Hazardous</option>
          <option value="Liquid">Liquid</option>
          <option value="Recyclable">Recyclable</option>
          <option value="Solid">Solid</option>
        </select>
      </div>
      <div>
        <label>Location</label>
        <div>
          <input
            placeholder="Latitude"
            type="number"
            value={props.location.lat}
            disabled={false}
            ref={latRef}
          />
          <input
            placeholder="Longitude"
            type="number"
            value={props.location.long}
            disabled={false}
            ref={longRef}
          />
        </div>
      </div>

      <div>
        <label>Add Image</label>
        <input
          type="file"
          id="img"
          name="img"
          accept="image/*"
          onChange={fileHandler}
        />
      </div>
      <div className={classes.btn}>
        <Button type="submit">Add Alert</Button>
      </div>
    </form>
  );
};

export default AddPost;
